import React, { useEffect, useState } from "react";
import Nav from "../components/Navbar/Nav";
import {
  Icon,
  Menu,
  Table,
  Button,
  Modal,
  Input,
  Dropdown,
  TableCell,
} from "semantic-ui-react";
import "./EntriesPageStyles.css";
import { useHistory } from "react-router";
import { useSelector } from "react-redux";

function EntriesPage({ match }) {
  //TODO GET FROM ENTRIES SLICE
  //CRUD SYSTEM ON ENTRIES SLICE
  const allEntries = useSelector((state) => state.birds);
  const [currentEntries, setcurrentEntries] = useState();

  useEffect(() => {
    const getBirdEntries = () => {
      allEntries.map((bird) => {
        if (
          bird.name == match.params.birdName &&
          bird.species == match.params.species_name
        ) {
          setcurrentEntries(bird.weights);
        }
      });
    };

    getBirdEntries();
  }, []);

  const data = match.params;
  const ts = new Date();
  const history = useHistory();

  //State of modal that adds new entry
  const [open, setOpen] = useState(false);
  const [weightinput, setWeight] = useState(0);
  const currentDate =
    ts.toLocaleDateString("en-US") + " " + ts.toLocaleTimeString("en-US");

  //State of modal that updates entry
  const [open2, setOpen2] = useState(false);
  const [newWeight, setnewWeight] = useState("");
  const [editId, seteditId] = useState("");

  //Server to add new entries
  const newEntries = async (e) => {
    if (weightinput <= 0) {
      alert("Incorrect Weight Value!");
      setWeight(0);
    } else {
      //create entry
    }
  };

  const sendEntry = () => {
    setOpen(false);
    newEntries();
  };

  // DISPLAYING ALL ENTRIES JSX
  const entries = currentEntries?.map((details, index) => {
    return (
      <div key={details.index} style={{ textAlign: "center", height: "100%" }}>
        <div className="EntriesTableRow">
          <p>{details.created_at}</p>
          <p>{details.weight}g</p>
          <Table.Cell>
            <Icon className="edit-button" name="edit" size="big" />
          </Table.Cell>
          <Table.Cell>
            <Icon
              className="delete-button"
              name="delete"
              size="big"
              color="red"
            />
          </Table.Cell>
        </div>
      </div>
    );
  });

  //For the input field for weight
  const options = [
    { key: "integer", text: "integer", value: "integer" },
    { key: "decimals", text: "decimals", value: "decimals" },
  ];

  const table = () => {
    return (
      <div className="TableContainer">
        <div className="HeaderContainer">
          <div>
            <p>TimeStamp</p>
          </div>
          <div>
            <p>Weight</p>
          </div>
          <div>
            <p>Edit</p>
          </div>
          <div>
            <p>Delete</p>
          </div>
        </div>
        <div className="allEntriesContainer">{entries}</div>
      </div>
    );
  };

  return (
    <div className="EntriesContainer">
      <Nav />
      <div className="EntriesWrapper">
        <div>{table()}</div>
      </div>
      {/* <Table celled className="EntriesTable">
        <Table.Header>
          <Table.Row style={{ textAlign: "center" }}>
            <Table.HeaderCell>TimeStamp</Table.HeaderCell>
            <Table.HeaderCell>Weight</Table.HeaderCell>
            <Table.HeaderCell>Edit</Table.HeaderCell>
            <Table.HeaderCell>Delete</Table.HeaderCell>
          </Table.Row>
        </Table.Header>
        {entries}
        <Table.Footer>
          <Table.Row>
            <Table.HeaderCell colSpan="4">
              <Menu floated="right" pagination>
                <Menu.Item as="a" icon>
                  <Icon name="chevron left" />
                </Menu.Item>
                <Menu.Item as="a">1</Menu.Item>
                <Menu.Item as="a">2</Menu.Item>
                <Menu.Item as="a">3</Menu.Item>
                <Menu.Item as="a">4</Menu.Item>
                <Menu.Item as="a" icon>
                  <Icon name="chevron right" />
                </Menu.Item>
              </Menu>
            </Table.HeaderCell>
          </Table.Row>
        </Table.Footer>
      </Table> */}

      {/* Modal To Add New Entry*/}
      <Modal
        size={"tiny"}
        onClose={() => setOpen(false)}
        onOpen={() => setOpen(true)}
        open={open}
        trigger={<Button className="showmodal--button">Add New Entry</Button>}
      >
        <Modal.Header>Add New Entry</Modal.Header>
        <Modal.Content>
          <Modal.Description>
            <Input
              label={<Dropdown defaultValue="int" options={options} />}
              labelPosition="right"
              placeholder="Enter weight"
              value={weightinput}
              onChange={(e) => setWeight(e.target.value)}
            />
          </Modal.Description>
        </Modal.Content>
        <Modal.Actions>
          <Button
            content="Add"
            labelPosition="right"
            icon="checkmark"
            onClick={sendEntry}
            positive
          />
        </Modal.Actions>
      </Modal>

      {/* Modal To Update Entry */}
      <Modal
        size={"tiny"}
        onClose={() => setOpen2(false)}
        onOpen={() => setOpen2(true)}
        open={open2}
      >
        <Modal.Header> Edit Entry</Modal.Header>
        <Modal.Content>
          <Modal.Description>
            <Input
              label={<Dropdown defaultValue="int" options={options} />}
              labelPosition="right"
              placeholder="Enter weight"
              value={newWeight}
              onChange={(e) => setnewWeight(e.target.value)}
            />
          </Modal.Description>
        </Modal.Content>
        <Modal.Actions>
          <Button
            content="Add"
            labelPosition="right"
            icon="checkmark"
            positive
          />
        </Modal.Actions>
      </Modal>

      <Button
        onClick={() => history.goBack()}
        content="Back"
        icon="left arrow"
        labelPosition="left"
      />
    </div>
  );
}

export default EntriesPage;
